import { useState, useEffect, useMemo } from "react";

function Testimonials() {
  const testimonials = useMemo(
    () => [
      {
        id: 1,
        name: "James Wilson",
        location: "Los Angeles, CA",
        image: "/placeholder.svg?height=80&width=80",
        text: "LUXEDRIVE exceeded all my expectations. The Ferrari I rented was immaculate, and the service was impeccable. Will definitely be using their services again for special occasions.",
        rating: 5,
      },
      {
        id: 2,
        name: "Sophia Chen",
        location: "New York, NY",
        image: "/placeholder.svg?height=80&width=80",
        text: "I rented a Bentley for my wedding day and it was absolutely perfect. The car arrived early, was spotless, and made for amazing photos. Thank you for making our day even more special!",
        rating: 5,
      },
      {
        id: 3,
        name: "Michael Rodriguez",
        location: "Miami, FL",
        image: "/placeholder.svg?height=80&width=80",
        text: "As a car enthusiast, I appreciate the excellent condition of their vehicles. The Lamborghini I rented was a dream to drive and the pickup/return process was seamless.",
        rating: 5,
      },
      {
        id: 4,
        name: "Emily Johnson",
        location: "Chicago, IL",
        image: "/placeholder.svg?height=80&width=80",
        text: "I've rented from several luxury car services, and LUXEDRIVE is by far the best. Their attention to detail and customer service is unmatched. The Rolls Royce was stunning!",
        rating: 5,
      },
    ],
    [] // Dependency array ensures `testimonials` is only created once
  );

  const [activeIndex, setActiveIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setActiveIndex((prevIndex) => (prevIndex + 1) % testimonials.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [testimonials.length]); // Only use `testimonials.length` as a dependency

  const handleDotClick = (index) => {
    setActiveIndex(index);
  };

  return (
    <section className="testimonials">
      <div className="container">
        <h2 className="section-title">What Our Clients Say</h2>
        <p className="section-subtitle">Hear from our satisfied customers about their luxury rental experience</p>

        <div className="testimonials-slider">
          <div className="testimonials-track" style={{ transform: `translateX(-${activeIndex * 100}%)` }}>
            {testimonials.map((testimonial) => (
              <div className="testimonial-card" key={testimonial.id}>
                <div className="testimonial-content">
                  <div className="quote-icon"></div>
                  <p className="testimonial-text">{testimonial.text}</p>

                  <div className="testimonial-rating">
                    {[...Array(5)].map((_, i) => (
                      <span key={i} className={`star ${i < testimonial.rating ? "filled" : ""}`}></span>
                    ))}
                  </div>
                </div>

                <div className="testimonial-author">
                  <div className="author-image">
                    <img src={testimonial.image || "/placeholder.svg"} alt={testimonial.name} />
                  </div>
                  <div className="author-info">
                    <h4 className="author-name">{testimonial.name}</h4>
                    <p className="author-location">{testimonial.location}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <div className="slider-dots">
            {testimonials.map((_, index) => (
              <button
                key={index}
                className={`dot ${index === activeIndex ? "active" : ""}`}
                onClick={() => handleDotClick(index)}
                aria-label={`Go to testimonial ${index + 1}`}
              ></button>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
}

export default Testimonials;
