"use client"

import { useState, useEffect } from "react"
import axios from "axios"
import "./EmployeeDashboard.css"


const OwnerDashboard = () => {
   const [user, setUser] = useState({ name: "", email: "" });
  const [car, setCar] = useState({
    brand: "",
    model: "",
    year: "",
    price: "",
    description: "",
    features: "",
    images: [],
    location: "",
  });
  const [imageFiles, setImageFiles] = useState([]);

  useEffect(() => {
    fetchUserDetails();
  }, []);

  const fetchUserDetails = async () => {
    const response = await axios.get("http://localhost:5000/api/users/USER_ID");
    setUser(response.data);
  };

  const updateUserDetails = async () => {
    await axios.put("http://localhost:5000/api/users/USER_ID", user);
    alert("User details updated!");
  }

  const addCar = async () => {
    const formData = new FormData();
    //Append non-image data
    Object.keys(car).forEach((key) => {
      if (key !== "images" && key !== "features") {
        formData.append(key, car[key]);
      }
    });
  
    // Handle features (ensure it's an array)
    if (Array.isArray(car.features)) {
      formData.append("features", JSON.stringify(car.features));
    }
  
    // Append images
    imageFiles.forEach((file) => {
      formData.append("images", file);
    });
  
    try {
       await axios.post("http://localhost:5000/api/cars/caruploads", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert("Car added successfully!");
       
     
    } catch (error) {
      console.error('Error:', error.response ? error.response.data : error);
      alert("Error adding car");
    }
    
   
  }
  
  return (
    <div>
      <h2>User Dashboard</h2>
      <input type="text" value={user.name} onChange={(e) => setUser({ ...user, name: e.target.value })} />
      <input type="email" value={user.email} onChange={(e) => setUser({ ...user, email: e.target.value })} />
      <button onClick={updateUserDetails}>Update Profile</button>

      <h3>Add New Car</h3>
      <input type="text" placeholder="Brand" onChange={(e) => setCar({ ...car, brand: e.target.value })} />
      <input type="text" placeholder="Model" onChange={(e) => setCar({ ...car, model: e.target.value })} />
      <input type="number" placeholder="Year" onChange={(e) => setCar({ ...car, year: e.target.value })} />
      <input type="number" placeholder="Price" onChange={(e) => setCar({ ...car, price: e.target.value })} />
      <textarea placeholder="Description" onChange={(e) => setCar({ ...car, description: e.target.value })} />
      <input type="text" placeholder="Features (comma separated)" onChange={(e) => setCar({ ...car, features: e.target.value.split(",") })} />
      <input type="file" multiple onChange={(e) => setImageFiles([...e.target.files])} />
      <button onClick={addCar}>Add Car</button>
    </div>
  );
}


export default  OwnerDashboard
