import { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import '../App.css';

const CarList = () => {
  const [cars, setCars] = useState([]);
  const [brand, setBrand] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [selectedCar, setSelectedCar] = useState(null);

  // Memoize fetchCars using useCallback
  const fetchCars = useCallback(async () => {
    try {
      let url = "http://localhost:5000/api/cars";

      if (brand || minPrice || maxPrice) {
        url += `?brand=${brand}&minPrice=${minPrice}&maxPrice=${maxPrice}`;
      }

      const response = await axios.get(url);
      setCars(response.data);
    } catch (error) {
      console.error("Error fetching cars:", error);
    }
  }, [brand, minPrice, maxPrice]); // Dependencies

  // Fetch cars only once on component mount or when filters change
  useEffect(() => {
    fetchCars();
  }, [fetchCars]);

  const handleCardClick = (car) => {
    setSelectedCar(car);
  };
  const closeDetails = () => {
    setSelectedCar(null);
  };

  return (
    <div className="w-full max-w-6xl mx-auto p-4">
    <h2 className="text-3xl font-bold text-center mb-6">Browse Luxury Cars </h2>
    <div className="bg-gray-100 p-4 rounded-lg mb-6 flex flex-wrap gap-4">
        <input
          type="text"
          placeholder="Brand"
          value={brand}
          onChange={(e) => setBrand(e.target.value)}
          className="p-2 rounded border border-gray-300 flex-1"
        />
        <input
          type="number"
          placeholder="Min Price"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
          className="p-2 rounded border border-gray-300 flex-1"
        />
        <input
          type="number"
          placeholder="Max Price"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
          className="p-2 rounded border border-gray-300 flex-1"
        />
        <button onClick={fetchCars}
         className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >Search</button>
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {cars.map((car) => (
          <div 
            key={car._id} 
            className="bg-white rounded-lg overflow-hidden shadow-lg transition-transform duration-300 hover:shadow-xl hover:scale-105 cursor-pointer"
            onClick={() => handleCardClick(car)}
          >
            <img
              src={car.images[0]}
              alt={`${car.brand} ${car.model}`}
              className="w-full h-48 object-cover"
            />
            <div className="p-4">
              <h3 className="text-xl font-semibold">{car.brand} {car.model}</h3>
              <p className="text-gray-600">{car.year} · ${car.price.toLocaleString()}</p>
            </div>
          </div>
        ))}
      </div>

      {selectedCar && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-3xl w-full max-h-full overflow-y-auto">
            <div className="p-6">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-2xl font-bold">{selectedCar.brand} {selectedCar.model}</h2>
                <button 
                  onClick={closeDetails}
                  className="text-gray-600 hover:text-gray-900 text-xl"
                >
                  ×
                </button>
              </div>
              
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <img 
                    src={selectedCar.images[0]} 
                    alt={`${selectedCar.brand} ${selectedCar.model}`}
                    className="w-full h-64 object-cover rounded-lg"
                  />
                </div>
                
                <div>
                  <p className="mb-2"><span className="font-semibold">Year:</span> {selectedCar.year}</p>
                  <p className="mb-2"><span className="font-semibold">Price:</span> ${selectedCar.price.toLocaleString()}</p>
                  <p className="mb-2"><span className="font-semibold">Description:</span> {selectedCar.description}</p>
                  <p className="mb-2">
                    <span className="font-semibold">Features:</span><br />
                    <ul className="list-disc pl-5 mt-1">
                      {selectedCar.features.map((feature, index) => (
                        <li key={index}>{feature}</li>
                      ))}
                    </ul>
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CarList;
