import { useState } from "react";
import { motion } from "framer-motion";
import { FaStar, FaQuoteLeft } from "react-icons/fa";
import test1 from "../assets/test1.jpg";
import test2 from "../assets/test2.jpg";
import test3 from "../assets/test3.jpg";
import test4 from "../assets/test4.jpg";

const testimonials = [
  {
    id: 1,
    name: "Alex Johnson",
    role: "Car Owner",
    image: test1,
    content:
      "LuxRide has transformed how I rent out my Tesla. The AI pricing ensures I'm always competitive, and I've seen a 40% increase in bookings since joining.",
    rating: 5,
  },
  {
    id: 2,
    name: "Sarah Williams",
    role: "Frequent Renter",
    image: test2,
    content:
      "I travel for business weekly and LuxRide has become my go-to. The real-time availability and instant booking save me hours of planning time.",
    rating: 5,
  },
  {
    id: 3,
    name: "Michael Chen",
    role: "Car Owner",
    image: test3,
    content:
      "The secure payment system and verified renter profiles give me peace of mind when renting out my luxury vehicles. Excellent platform!",
    rating: 4,
  },
  {
    id: 4,
    name: "Emily Rodriguez",
    role: "Weekend Renter",
    image: test4,
    content:
      "Found a gorgeous Porsche for my anniversary weekend at a surprisingly reasonable price. The booking process was seamless and the car was immaculate.",
    rating: 5,
  },
];

export function TestimonialsSection() {
  const [selected, setSelected] = useState(0);

  return (
    <section className="py-16 bg-gray-100">
      <div className="container mx-auto text-center">
        <h2 className="text-3xl font-bold text-blue-600 mb-4">What Our Users Say</h2>
        <p className="text-gray-600 mb-10">Hear from car owners and renters who have experienced our platform.</p>
        <motion.div
          key={testimonials[selected].id}
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.5 }}
          className="bg-white shadow-lg rounded-2xl p-8 max-w-2xl mx-auto"
        >
          <div className="flex items-center gap-4">
            <img
              src={testimonials[selected].image}
              alt={testimonials[selected].name}
              className="w-16 h-16 rounded-full object-cover border-2 border-blue-500"
            />
            <div className="text-left">
              <h3 className="text-xl font-semibold">{testimonials[selected].name}</h3>
              <p className="text-gray-500">{testimonials[selected].role}</p>
            </div>
          </div>
          <div className="flex mt-4 mb-4">
            {[...Array(5)].map((_, i) => (
              <FaStar key={i} className={i < testimonials[selected].rating ? "text-yellow-400" : "text-gray-300"} />
            ))}
          </div>
          <p className="text-gray-700 text-lg"><FaQuoteLeft className="text-blue-500 inline-block mr-2" />{testimonials[selected].content}</p>
        </motion.div>
        <div className="flex justify-center gap-2 mt-6">
          {testimonials.map((_, index) => (
            <button
              key={index}
              onClick={() => setSelected(index)}
              className={`w-4 h-4 rounded-full transition-all ${selected === index ? "bg-blue-500 w-6" : "bg-gray-300"}`}
            ></button>
          ))}
        </div>
      </div>
    </section>
  );
}
